import React from 'react';
import {Form, Button, Alert, Col} from 'react-bootstrap';

class CreatePage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            movies: [],
            movie: {
                name: "",
                rating:"",
                time:""
            }
        }
        this.handleSelect = this.handleSelect.bind(this);
    }

    handleSelect(event){
        //alert(event.target.value);
        this.setState({movie: event.target.value})
    }

    componentDidMount(){
        fetch('http://localhost:5000/movie/all')
            .then(async res => await res.json())
            .then(res => this.setState({movies: res.data}));
    }

    handleSubmit(event){
        event.preventDefault();

        let data = {
            name: this.state.name,
            rating: this.state.rating
        }
        //console.log(this.state);
        console.log(data);

        fetch('http://localhost:5000/movie/create', {
            headers: {'Content-Type':'application/json'},
            method: 'POST',
            body: JSON.stringify(data)
        })
            .then(async res => await res.json())
            .then(res => this.setState({data: res.data, show: !this.state.show}));

    }

    render(){
        return(
            <Form onSubmit={this.handleSubmit.bind(this)}>
                <Alert variant="info">
                    <h2><Alert.Link>Update</Alert.Link> movie page</h2>
                </Alert>
                <Form.Group controlId='movieSelect' onChange={this.handleSelect}>
                    <Form.Row>
                        <Form.Label column lg={2}>Select movie</Form.Label>
                        <Col>
                            <Form.Control as='select'>
                                {this.state.movies.map((item, index)=>{
                                    return(
                                        <option value={item}>{item.name}</option>
                                    )
                                })}
                            </Form.Control>
                        </Col>
                    </Form.Row>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Name</Form.Label>
                    <Form.Control type='text' value={this.state.movie.name} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Rating</Form.Label>
                    <Form.Control type='text' value={this.state.movie.rating} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Time</Form.Label>
                    <Form.Control>
                    <Form.Control type='text' value={this.state.movie.time} />
                    </Form.Control>
                </Form.Group>
                <Button variant='primary' type='submit' onClick={this.createMovie}>Submit</Button>{' '}
                <Button variant='danger'>Clear form</Button>{' '}
                <Button variant='secondary'>Cancel</Button>
            </Form>
        )
    }
}

export default CreatePage;
