import React from 'react';
import {Form, Button, Alert} from 'react-bootstrap';

class CreatePage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            movies: [],
            movie: {}
        }
        this.handleName = this.handleName.bind(this);
        this.handleRating = this.handleRating.bind(this);
    }

    handleName(event){
        this.setState({name: event.target.value});
    }

    handleRating(event){
        this.setState({rating: event.target.value});
    }

    handleSubmit(event){
        event.preventDefault();

        let data = {
            name: this.state.name,
            rating: this.state.rating
        }
    }

    componentDidMount(){
        fetch('http://localhost:5000/movie/all')
            .then(async res => await res.json())
            .then(res => this.setState({movies: res.data}));
    }

    render(){
        return(
            <Form onSubmit={this.handleSubmit.bind(this)}>
                <Alert variant="info">
                    <h2><Alert.Link>Update</Alert.Link> movie page</h2>
                </Alert>
                <Form.Group>
                    <Form.Row>
                        <Form.Label>Select movie</Form.Label>
                        <Form.Control as='select' custom>
                            {this.state.movies.map((item, index)=>{
                                return(
                                    <option>{item.name}</option>
                                )
                            })}
                        </Form.Control>
                    </Form.Row>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Rating</Form.Label>
                    <Form.Control type='text' placeholder='Enter the movie rating' onChange={this.handleRating}/>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Time</Form.Label>
                    <Form.Control>

                    </Form.Control>
                </Form.Group>
                <Button variant='primary' type='submit' onClick={this.createMovie}>Submit</Button>{' '}
                <Button variant='danger'>Clear form</Button>{' '}
                <Button variant='secondary'>Cancel</Button>
            </Form>
        )
    }
}

export default CreatePage;
